    'SELECT * FROM `users` ',
    function (err, results, fields) {
        console.log('>>> check mysql')
        console.log(results); // results contains rows returned by server
        let rows = results.map((row) => { return row.id })
        // console.log(results[0]);
        console.log(rows)
    }
);

